import React, { useState, useEffect } from "react";
import axios from "axios";
import ItemsList from "../ItemsList";
import PropTypes from "prop-types";
import { FaUserTag } from "react-icons/fa";
import AddItemModal from "../Modals/AddItemModal";



const Items = ({ WEMES_URL }) => {
  const [transactionData, setItemData] = useState([]);
  const [addItemModalShow, setAddItemModalShow] = useState(false);

  const getItems = () => {
    axios
      .get(`${WEMES_URL}transactions/`)
      .then((response) => {
        const newData = response.data.map((transaction) => {
          return {
            id: transaction.id,
            drop_off: transaction.drop_off,
            admin: transaction.admin,
            customer: transaction.customer,
            items: transaction.items,
            description: transaction.description,
          };
        });
        setItemData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };


  // useEffect(() => getItems(), [transactionData]);
  useEffect(() => getItems(), [transactionData]);
  return (
    <div>
      <h1>Items</h1>
      <FaUserTag
        title="add an account"
        size={50}
        variant="warning"
        onClick={() => setAddItemModalShow(true)}
      />

      <AddItemModal
        show={addItemModalShow}
        onHide={() => setAddItemModalShow(false)}
        WEMES_URL={WEMES_URL}
      />
      <ItemsList WEMES_URL={WEMES_URL} transactionData={transactionData} />
    </div>
  );
};

Items.propTypes = {
  WEMES_URL: PropTypes.string.isRequired,
};

export default Items;
