import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import NaviBar from "./Components/NaviBar";

// export const WEMES_URL = "https://wemes-be.herokuapp.com/";
export const WEMES_URL = "http://127.0.0.1:8000/";

const App = () => {
  const [transactionsData, setTransactionData] = useState([]);
  const [accountData, setAccountData] = useState([]);

  const getAccounts = () => {
    console.log(WEMES_URL);
    axios
      .get(`${WEMES_URL}users/`)
      .then((response) => {
        console.log(response);
        const newData = response.data.map((account) => {
          return {
            id: account.id,
            first_name: account.first_name,
            last_name: account.last_name,
            last_four: account.last_four,
            phone_num: account.phone_num,
            email: account.email,
            code: account.code,
            admin: account.admin,
            is_active: account.is_active,
            transactions: account.transactions,
          };
        });
        setAccountData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };





  const getTransactions = () => {
    axios
      .get(`${WEMES_URL}transactions/`)
      .then((response) => {
        console.log(response);
        const newData = response.data.map((transaction) => {
          return {
            id: transaction.id,
            drop_off: transaction.drop_off,
            admin: transaction.admin,
            customer: transaction.customer,
            items: transaction.items,
            description: transaction.description,
          };
        });
        setTransactionData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };

  useEffect(() => getTransactions(), []);

return <div className="App">
    <NaviBar 
    accounts={accountData} 
    transactions={transactionsData} 
    />
  </div>;
}

export default App;
