import React, { useState } from "react";
import PropTypes from "prop-types";
import axios from "axios";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


const AddAccountForm = () => {
  const addAccount = ({ first_name, last_name, phone_num, email }) => {
    if (!first_name) {
        return alert('New account must have a first name!');
    }
    if (!last_name) {
        return alert('New account must have an last name!');
    }
    if (!phone_num) {
        return alert('New account must have a phone num!');
    }
    if (!email) {
        return alert('New account must have an email!');
    }
    axios
        .post(`${URL}/users`, {
            first_name: first_name,
            last_name: last_name,
            phone_num: phone_num,
            email: email,
        })
        .then((response) => {})
        .catch((error) => {
            console.log(error);
        });
};

  const [AccountData, setAccountData] = useState({
    first_name: "",
    last_name: "",
    phone_num: "",
    email: "",
  });

  const submitAccountData = (event) => {
    event.preventDefault();
    addAccount(AccountData);
    setAccountData({ first_name: "", first_last: "", phone_num: "", email: "", });
  };

  const handleAccountChange = (event) => {
    setAccountData({ ...AccountData, [event.target.name]: event.target.value });
  };

  return (
    <Form size="lg" onSubmit={submitAccountData}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>First Name</Form.Label>
        <Form.Control type="name" placeholder="First Name" onChange=""/>
        <Form.Text className="text-muted">
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Last Name</Form.Label>
        <Form.Control type="name" placeholder="Last Name" />
        <Form.Text className="text-muted">
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" />
        <Form.Text className="text-muted">
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password" placeholder="Password" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
    // <div>
