import React, { useState } from "react";
import Table from "react-bootstrap/Table";
import PropTypes from "prop-types";
import SortMenuAccounts from "./SortMenuAccounts";

const ListAccounts = ({ accountData }) => {
  const [modalShow, setModalShow] = useState(false);
  const [clickedIndex, setClickedIndex] = useState(0);
  const [sortBy, setSortBy] = useState("id");
  const [orderBy, setOrderBy] = useState("desc");

  const sortedAccounts = accountData.sort((a, b) => {
    let order = orderBy === "asc" ? 1 : -1;
    let sortByA = sortBy === "id" ? a[sortBy] : a[sortBy];
    let sortByB = sortBy === "id" ? b[sortBy] : b[sortBy];
    return sortByA < sortByB ? -1 * order : 1 * order;
  });

  const accountInfo = () => {
    return sortedAccounts.map((account, index) => (
      <tr
        key={index}
        onClick={() => {
          setClickedIndex(index);
          setModalShow(true);
        }}
      >
        <td>{account.customer}</td>
        <td>{account.admin}</td>
        <td>{account.drop_off}</td>
        <td>{account.description}</td>
        <td>{account.items.length}</td>
      </tr>
    ));
  };

  return (
    <>
      <div>
        <DisplayAccountModal
          show={modalShow}
          onHide={() => setModalShow(false)}
          wemes_url={wemes_url}
          index={clickedIndex}
        />
        <SortMenuAccounts
          sortBy={sortBy}
          onSortByChange={(sortOption) => {
            setSortBy(sortOption);
          }}
          orderBy={orderBy}
          onOrderByChange={(orderOption) => {
            setOrderBy(orderOption);
          }}
        />
      </div>
      <Table striped hover>
        <thead>
          <tr>
            <th>Account ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Accounts</th>
          </tr>
          </thead>
        <tbody>{accountInfo()}</tbody>
      </Table>
    </>
  );
};

ListAccounts.propTypes = {
  accounts: PropTypes.array,
};

export default ListAccounts;
