import React, { useState, useEffect } from "react";
import axios from "axios";
import ListItems from "../ListItems";
import PropTypes from "prop-types";
import { FaUserTag } from "react-icons/fa";
import AddItemModal from "../Modals/AddItemModal";

const Items = ({ wemes_url }) => {
  const [itemData, setItemData] = useState([]);
  const [addItemModalShow, setAddItemModalShow] = useState(false);

  const getItems = () => {
    axios
      .get(`${wemes_url}items/`)
      .then((response) => {
        const newData = response.data.map((item) => {
          return {
            id: item.id,
            drop_off: item.drop_off,
            admin: item.admin,
            customer: item.customer,
            items: item.items,
            description: item.description,
          };
        });
        setItemData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };

  // useEffect(() => getItems(), [itemData]);
  useEffect(() => getItems(), [itemData]);
  return (
    <div>
      <h1>Items</h1>
      <FaUserTag
        title="add an account"
        size={50}
        variant="warning"
        onClick={() => setAddItemModalShow(true)}
      />

      <AddItemModal
        show={addItemModalShow}
        onHide={() => setAddItemModalShow(false)}
        wemes_url={wemes_url}
      />
      <ListItems
        wemes_url={wemes_url}
        itemData={itemData}
      />
    </div>
  );
};

Items.propTypes = {
  wemes_url: PropTypes.string.isRequired,
};

export default Items;
