import React, { useState } from "react";
import PropTypes from "prop-types";

const AddAccount = ({ onAddBoardCallback }) => {
  const [transactionData, setBoardData] = useState({
    first_name: "",
    last_name: "",
    last_four: "",
    phone_num: "",
    email: "",
    admin: "",
    is_active: "",
  });

  const submitBoardData = (event) => {
    event.preventDefault();
    onAddBoardCallback(transactionData);
    setBoardData({ title: "", owner: "" });
  };

  const handleBoardChange = (event) => {
    setBoardData({ ...transactionData, [event.target.name]: event.target.value });
  };

  return (
    <div>
      <div className={shown}>
        <form onSubmit={submitBoardData} className="new-transaction__form">
          <section>
            <h3>Add New Board</h3>
            <div className="new-transaction__fields">
              <div>
                <label htmlFor="title">Title</label>
                <input
                  name="title"
                  id="title"
                  value={transactionData.title}
                  onChange={handleBoardChange}
                />
              </div>
              <div>
                <label htmlFor="owner">Owner</label>
                <input
                  name="owner"
                  id="owner"
                  value={transactionData.owner}
                  onChange={handleBoardChange}
                />
              </div>
              <button className="button new-transaction__submit" type="submit">
                Add Board
              </button>
            </div>
          </section>
        </form>
      </div>
    </div>
  );
};

AddAccount.propTypes = {
  onAddBoardCallback: PropTypes.func.isRequired,
};

export default AddAccount;
