import React, { useState } from "react";
import PropTypes from "prop-types";
import axios from "axios";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


const AddAccountForm = () => {
  const addAccount = ({ first_name, last_name, phone_num, email }) => {
    if (!first_name) {
        return alert('New account must have a first name!');
    }
    if (!last_name) {
        return alert('New account must have an last name!');
    }
    if (!phone_num) {
        return alert('New account must have a phone num!');
    }
    if (!email) {
        return alert('New account must have an email!');
    }
    axios
        .post(`${URL}/users`, {
            first_name: first_name,
            last_name: last_name,
            phone_num: phone_num,
            email: email,
        })
        .then((response) => {})
        .catch((error) => {
            console.log(error);
        });
};

  const [AccountData, setAccountData] = useState({
    first_name: "",
    last_name: "",
    phone_num: "",
    email: "",
  });

  const submitAccountData = (event) => {
    event.preventDefault();
    addAccount(AccountData);
    setAccountData({ first_name: "", first_last: "", phone_num: "", email: "", });
  };

  const handleAccountChange = (event) => {
    setAccountData({ ...AccountData, [event.target.name]: event.target.value });
  };

  return (
    // <Form size="lg">
    //   <Form.Group className="mb-3" controlId="formBasicEmail">
    //     <Form.Label>First Name</Form.Label>
    //     <Form.Control type="name" placeholder="First Name" />
    //     <Form.Text className="text-muted">
    //     </Form.Text>
    //   </Form.Group>

    //   <Form.Group className="mb-3" controlId="formBasicEmail">
    //     <Form.Label>Last Name</Form.Label>
    //     <Form.Control type="name" placeholder="Last Name" />
    //     <Form.Text className="text-muted">
    //     </Form.Text>
    //   </Form.Group>

    //   <Form.Group className="mb-3" controlId="formBasicEmail">
    //     <Form.Label>Email address</Form.Label>
    //     <Form.Control type="email" placeholder="Enter email" />
    //     <Form.Text className="text-muted">
    //     </Form.Text>
    //   </Form.Group>

    //   <Form.Group className="mb-3" controlId="formBasicPassword">
    //     <Form.Label>Password</Form.Label>
    //     <Form.Control type="password" placeholder="Password" />
    //   </Form.Group>
    //   <Form.Group className="mb-3" controlId="formBasicCheckbox">
    //     <Form.Check type="checkbox" label="Check me out" />
    //   </Form.Group>
    //   <Button variant="primary" type="submit">
    //     Submit
    //   </Button>
    // </Form>
    <div>
        <form onSubmit={submitAccountData} className="new-Account__form">
          <section>
            <h3>Add New Account</h3>
            <div className="new-Account__fields">
              <div>
                <label htmlFor="first_name">First Name</label>
                <input
                  name="first_name"
                  id="first_name"
                  value={AccountData.first_name}
                  onChange={handleAccountChange}
                />
              </div>
              <div>
                <label htmlFor="first_last">Last Name</label>
                <input
                  name="first_last"
                  id="first_last"
                  value={AccountData.first_last}
                  onChange={handleAccountChange}
                />
              </div>
              <div>
                <label htmlFor="phone_num">Phone Number</label>
                <input
                  name="phone_num"
                  id="phone_num"
                  value={AccountData.phone_num}
                  onChange={handleAccountChange}
                />
              </div>
              <div>
                <label htmlFor="email">Last Name</label>
                <input
                  name="email"
                  id="email"
                  value={AccountData.email}
                  onChange={handleAccountChange}
                />
              </div>
              <button className="button new-Account__submit" type="submit">
                Add Account
              </button>
            </div>
          </section>
        </form>
      </div>
  );
};

AddAccountForm.propTypes = {
  onAddAccountFormCallback: PropTypes.func.isRequired,
};

export default AddAccountForm;
