import React, { useState } from "react";
import Table from "react-bootstrap/Table";
import PropTypes from "prop-types";
import SortTransactionMenu from "../Components/SortTransactionMenu";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";

// DISPLAY TRANSACTION
const DisplayTransactionModal = (props) => {
  const [transactionCustomer, setTransactionCustomer] = useState(0);
  const [transactionAdmin, setTransactionAdmin] = useState(0);
  const [transactionDate, setTransactionDate] = useState();
  const [transactionData, setTransactionData] = useState({
    message: "",
    item: [],
  });

  const transaction_url = `${props.WEMES_URL}transactions/${props.index + 1}/`

  axios
    .get(`${transaction_url}`)
    .then((response) => {
      console.log(response.data);
      setTransactionCustomer(response.data.customer);
      setTransactionDate(response.data.drop_off);
      setTransactionAdmin(response.data.admin);
    })
    .catch((err) => {
      alert(err);
    });

  const addItem = ({
    drop_off,
    due_date,
    transaction,
    type,
    color,
    is_shoe,
    follow_up,
    description,
    tag_id,
  }) => {
    axios
      .post(`${transaction_url}`, {
        drop_off: drop_off,
        due_date: due_date,
        transaction: transaction,
        type: type,
        color: color,
        is_shoe: is_shoe,
        follow_up: follow_up,
        description: description,
        tag_id: tag_id,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const [itemData, setItemData] = useState({
    drop_off: "",
    due_date: "",
    transaction: "",
    tag_id: "",
    type: "",
    color: "",
    is_shoe: "",
    follow_up: "",
    description: "",
  });

  const submitTransactionData = (event) => {
    event.preventDefault();
    addItem(transactionData);
    setTransactionData({
    drop_off: "",
    due_date: "",
    transaction: "",
    tag_id: "",
    type: "",
    color: "",
    is_shoe: "",
    follow_up: "",
    description: "",
    });
  };

  const submitItemData = (event) => {
    event.preventDefault();
    addItem(itemData);
    setItemData({
      first_name: "",
      last_name: "",
      phone_num: "",
      last_four: "",
      email: "",
      transactions: [],
    });
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          {transactionCustomer}'s Transaction by {transactionAdmin}
          <br />
          Dropped Off: {transactionDate}
          <Form size="lg" onSubmit={submitTransactionData}>
            <Form.Group className="mb-3" controlId="formDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="name"
                // placeholder="description"
                onChange={(event) =>
                  setTransactionData({
                    ...transactionData,
                    description: event.target.value,
                  })
                }
              />
              <Form.Text className="text-muted"></Form.Text>
            </Form.Group>
            <Button variant="warning" type="submit" onClick={props.onHide}>
              Add Item
            </Button>
          </Form>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>BODY </Modal.Body>
    </Modal>
  );
};

const TransactionsList = ({ transactionData, WEMES_URL }) => {
  const [modalShow, setModalShow] = useState(false);
  const [clickedIndex, setClickedIndex] = useState(0);
  const [sortBy, setSortBy] = useState("id");
  const [orderBy, setOrderBy] = useState("desc");

  const sortedTransactions = transactionData.sort((a, b) => {
    let order = orderBy === "asc" ? 1 : -1;
    let sortByA = sortBy === "id" ? a[sortBy] : a[sortBy];
    let sortByB = sortBy === "id" ? b[sortBy] : b[sortBy];
    return sortByA < sortByB ? -1 * order : 1 * order;
  });

  const transactionInfo = () => {
    return sortedTransactions.map((transaction, index) => (
      <tr
        key={index}
        onClick={() => {
          setClickedIndex(index);
          setModalShow(true);
        }}
      >
        <td>{transaction.customer}</td>
        <td>{transaction.admin}</td>
        <td>{transaction.drop_off}</td>
        <td>{transaction.description}</td>
        <td>{transaction.items.length}</td>
      </tr>
    ));
  };

  return (
    <>
      <div>
        <DisplayTransactionModal
          show={modalShow}
          onHide={() => setModalShow(false)}
          WEMES_URL={WEMES_URL}
          index={clickedIndex}
        />
        <SortTransactionMenu
          sortBy={sortBy}
          onSortByChange={(sortOption) => {
            setSortBy(sortOption);
          }}
          orderBy={orderBy}
          onOrderByChange={(orderOption) => {
            setOrderBy(orderOption);
          }}
        />
      </div>
      <Table striped hover>
        <thead>
          <tr>
            <th>Customer</th>
            <th>Admin</th>
            <th>Dropped Off</th>
            <th>Message</th>
            <th>Items</th>
          </tr>
        </thead>
        <tbody>{transactionInfo()}</tbody>
      </Table>
    </>
  );
};

TransactionsList.propTypes = {
  transactions: PropTypes.array,
};

export default TransactionsList;
