import React, { useState } from "react";
import PropTypes from "prop-types";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const AddAccountForm = ({WEMES_URL}) => {
  const addAccount = ({ first_name, last_name, phone_num, email }) => {
    // if (!first_name) {
    //     return alert('New account must have a first name!');
    // }
    // if (!last_name) {
    //     return alert('New account must have an last name!');
    // }
    // if (!phone_num) {
    //     return alert('New account must have a phone num!');
    // }
    // if (!email) {
    //     return alert('New account must have an email!');
    // }
    axios
      .post(`${WEMES_URL}/users`, {
        first_name: first_name,
        last_name: last_name,
        phone_num: phone_num,
        email: email,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const [AccountData, setAccountData] = useState({
    first_name: "",
    last_name: "",
    phone_num: "",
    email: "",
  });

  const submitAccountData = (event) => {
    event.preventDefault();
    addAccount(AccountData);
    setAccountData({
      first_name: "",
      first_last: "",
      phone_num: "",
      last_four: "",
      email: "",
      transactions: [],
    });
  };

  const handleAccountChange = (event) => {
    setAccountData({ ...AccountData, [event.target.name]: event.target.value });
  };

  return (
    <Form size="lg" onSubmit={submitAccountData}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>First Name</Form.Label>
        <Form.Control
          type="name"
          placeholder="First Name"
          onChange={handleAccountChange}
        />
        <Form.Text className="text-muted"></Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Last Name</Form.Label>
        <Form.Control
          type="name"
          placeholder="Last Name"
          onChange={handleAccountChange}
        />
        <Form.Text className="text-muted"></Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          onChange={handleAccountChange}
        />
        <Form.Text className="text-muted"></Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Phone Number</Form.Label>
        <Form.Control
          type="number"
          placeholder="Phone Number"
          onChange={handleAccountChange}
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Last Four</Form.Label>
        <Form.Control
          type="number"
          placeholder="Last Number"
          onChange={handleAccountChange}
        />
      </Form.Group>
      {/* <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group> */}
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
    // <div>
    //     <form onSubmit={submitAccountData} className="new-Account__form">
    //       <section>
    //         <h3>Add New Account</h3>
    //         <div className="new-Account__fields">
