import React, { useState, useEffect } from "react";
import axios from "axios";
import ListAccounts from "../ListAccounts";
import PropTypes from "prop-types";
import { FaUserTag } from "react-icons/fa";
import AddAccountModal from "../Modals/AddAccountModal";

const Accounts = ({ wemes_url }) => {
  const [transactionData, setAccountData] = useState([]);
  const [addAccountModalShow, setAddAccountModalShow] = useState(false);

  const getAccounts = () => {
    axios
      .get(`${wemes_url}transactions/`)
      .then((response) => {
        const newData = response.data.map((transaction) => {
          return {
            id: transaction.id,
            drop_off: transaction.drop_off,
            admin: transaction.admin,
            customer: transaction.customer,
            items: transaction.items,
            description: transaction.description,
          };
        });
        setAccountData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };

  // useEffect(() => getAccounts(), [transactionData]);
  useEffect(() => getAccounts(), [transactionData]);
  return (
    <div>
      <h1>Accounts</h1>
      <FaUserTag
        title="add an account"
        size={50}
        variant="warning"
        onClick={() => setAddAccountModalShow(true)}
      />

      <AddAccountModal
        show={addAccountModalShow}
        onHide={() => setAddAccountModalShow(false)}
        wemes_url={wemes_url}
      />
      <ListAccounts
        wemes_url={wemes_url}
        transactionData={transactionData}
      />
    </div>
  );
};

Accounts.propTypes = {
  wemes_url: PropTypes.string.isRequired,
};

export default Accounts;
