import React, { useState, useEffect } from "react";
import axios from "axios";
import ListAccounts from "../ListAccounts";
import PropTypes from "prop-types";
import { FaUserTag } from "react-icons/fa";
import AddAccountModal from "../Modals/AddAccountModal";
import Items from "./Items";

const Accounts = ({ wemes_url }) => {
  const [accountData, setAccountData] = useState([]);
  const [addAccountModalShow, setAddAccountModalShow] = useState(false);

  const getAccounts = () => {
    axios
      .get(`${wemes_url}accounts/`)
      .then((response) => {
        const newData = response.data.map((account) => {
          return {
            id: account.id,
            drop_off: account.drop_off,
            admin: account.admin,
            customer: account.customer,
            items: account.items,
            description: account.description,
          };
        });
        setAccountData(newData);
      })
      .catch((err) => {
        alert(err);
      });
  };

  // useEffect(() => getAccounts(), [accountData]);
  useEffect(() => getAccounts(), [accountData]);
  return (
    <div>
      <h1>Accounts</h1>
      <FaUserTag
        title="add an account"
        size={50}
        variant="warning"
        onClick={() => setAddAccountModalShow(true)}
      />

      <AddAccountModal
        show={addAccountModalShow}
        onHide={() => setAddAccountModalShow(false)}
        wemes_url={wemes_url}
      />
      <ListAccounts
        wemes_url={wemes_url}
        accountData={accountData}
      />
    </div>
  );
};

Items.propTypes = {
  wemes_url: PropTypes.string.isRequired,
};

export default Items;
